    decimal CheckPrice(decimal prId)
    {
        ModelContext context = new ModelContext();
        var furniture =  context.Furnitures.Where(fur => fur.Id == prId).FirstOrDefault();
        var offer = context.Offers.Where(ofr => ofr.FurnitureId == prId && ofr.EndDate >= DateTime.Today).OrderByDescending(ofr => ofr.Id).FirstOrDefault();
        decimal price = furniture.Price;
        if (offer != null) price = price - offer.Percentage * price / 100;
        return price;
    }

